import React, {useState,useEffect,Fragment} from "react";
import axios from "axios"
import { Component } from 'react';
import styled from "styled-components";
import { redirect } from "react-router-dom";

const Field = styled.div`
    border-radius: 4px;
    input{
        min-height: 50px;
        border-radius: 4px;
        border: 1px solid grey;
        margin: 0 0 12px 0 ;
        padding: 12px;
        width: 100%;
    }
    textarea{
        width: 100%;
        min-height: 80px;
        border-radius: 4px;
        border: 1px solid grey;
        margin: 12px 0 ;
        padding: 12px;
    }
`
const Wrapper = styled.div`
    padding: 20px;
    background:black;
    height: 100vh;
    padding-top: 100px;
    border-radius: 20px;
`
const SubmitButton = styled.button`
    color: white;
    background: #333;
    border-radius: 4px;
    border 1px solid white;
    padding: 12px;
    font-size: 18px;
    cursor: pointer;
    transition: ease-in-out 0.1s;
    width: 100%;
    margin-top: 20px

    &:hover {
        background: white;
        color:black;
        border: 1px solid white;
    }
`

const Headline = styled.div`
    padding: 20px;
    text-align: center;
    font-size: 30px;
    font-weight: bold;
    color:white;
`
class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { 
      username: '',
      email: '',
      password: '',
      errors: ''
     };
  }
handleChange = (event) => {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })
  };
handleSubmit = (event) => {
    event.preventDefault()
  };
render() {
    const {email, password} = this.statereturn (
        <Wrapper>
            <form onSubmit={handleSubmit}>
                <Headline>Log In!</Headline>
                <Field>
                    <input onChange={this.handleChange} value = {email} type="text" name="email" placeholder="Please type your Email"/>
                </Field>
                <Field>
                    <input onChange={this.handleChange} value = {password} type="password" name="password" placeholder="Please type your Password"/>
                </Field>
                <SubmitButton type="submit">Log In</SubmitButton>
            </form>
        </Wrapper>
    );
  }
}
export default Login;
