import React, {Fragment} from "react";
import styled from "styled-components";

const ratingContainer = styled.div`
    text-align: center;
    border-radius: 4px;
    font-size: 18px;
    padding: 40px 0 10px 0;
    border: 1px solid grey;
    background: white;
`
const ratingBox = styled.div``
const ratingTitle = styled.div``

const ReviewForm = (props) => {
    const ratingOptions = [5,4,3,2,1].map( (score, index) => {
        return(
            <Fragment>
                <input type="radio" value={score} name="rating" onChange={() => console.log("selected", score)} id={`rating-${score}`}/>
                <label></label>
            </Fragment>
        )
    })

    return(
    <div className="Wrapper">
        <form onSubmit={props.handleSubmit}>
            <div>Have an opinion of the match: {props.attributes.name}? Share it!</div>
            <div className="field">
                <input onChange={props.handleChange} value = {props.review.title} type="text" name="title" placeholder="Review Title"/>
            </div>
            <div className="field">
                <input onChange={props.handleChange} value = {props.review.description} type="text" name="description" placeholder="Review Title"/>
            </div>
            <div className="field">
                <ratingContainer>
                    <ratingTitle>Rate this Match</ratingTitle>
                    <ratingBox>
                        {ratingOptions}
                    </ratingBox>
                </ratingContainer>
            </div>
            <button type="submit">Submit your Review</button>
        </form>
    </div>
    )
}

export default ReviewForm
