import React, {useState,useEffect,Fragment, Component} from "react";
import axios from "axios"
import styled from "styled-components";
import { redirect } from "react-router-dom";

const Field = styled.div`
    border-radius: 4px;
    input{
        min-height: 50px;
        border-radius: 4px;
        border: 1px solid grey;
        margin: 0 0 12px 0 ;
        padding: 12px;
        width: 100%;
    }
    textarea{
        width: 100%;
        min-height: 80px;
        border-radius: 4px;
        border: 1px solid grey;
        margin: 12px 0 ;
        padding: 12px;
    }
`
const Wrapper = styled.div`
    padding: 20px;
    background:black;
    height: 100vh;
    padding-top: 100px;
    border-radius: 20px;
`
const SubmitButton = styled.button`
    color: white;
    background: #333;
    border-radius: 4px;
    border 1px solid white;
    padding: 12px;
    font-size: 18px;
    cursor: pointer;
    transition: ease-in-out 0.1s;
    width: 100%;
    margin-top: 20px

    &:hover {
        background: white;
        color:black;
        border: 1px solid white;
    }
`

const Headline = styled.div`
    padding: 20px;
    text-align: center;
    font-size: 30px;
    font-weight: bold;
    color:white;
`

class Sessions extends Component  {
    //const [user, setUser] = useState({email: "", password: ""})
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            password: "",
            errors: ""
        };
    }

    handleChange = (e) => {
        const {name, value} = e.target
        this.setState({
            [name]: value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault()

        const user = {
            email: email,
            password: password
          }

        axios.post("http://localhost:3000/api/v1/login", {user}, {withCredentials:true})
        .then(resp => {
            if(resp.data.logged_in){
                debugger
                this.handleLogin(resp.data)
                this.redirect()
            } 
            else {
                debugger
                this.setState({
                    errors: resp.data.errors
                })
            }
        })
        .catch(error => console.log("api errors: ", error))
    }
    redirect = () => {window.location.href = "/"}

    handleErrors = () => {
        return (
            <div>
              <ul>
              {this.state.errors.map(error => {return <li key={error}>{error}</li>})}
              </ul>
            </div>
        )
    }

    render(){
        const {email, password} = this.state
    return(
        <Wrapper>
            <form onSubmit={this.handleSubmit}>
                <Headline>Log In!</Headline>
                <Field>
                    <input onChange={this.handleChange} value = {user.email} type="text" name="email" placeholder="Please type your Email"/>
                </Field>
                <Field>
                    <input onChange={this.handleChange} value = {user.password} type="password" name="password" placeholder="Please type your Password"/>
                </Field>
                <SubmitButton type="submit">Log In</SubmitButton>
            </form>
        </Wrapper>
        )
    }
    }
export default Sessions

